      j++;
    }
  }

  return commonElements;
}

// Example usage
const arr1 = [1, 3, 5, 7, 9];
const arr2 = [2, 4, 5, 7, 9, 10];
const commonElements = findCommonElements(arr1, arr2);
console.log(commonElements); // Output: [5, 7, 9]
```

