#include <iostream>
#include <map>
using namespace std;

// Map to store already calculated Fibonacci numbers
map<int, long long> memo;

// Function to find nth Fibonacci number using recursion with memoization
long long fibonacci(int n) {
    // Base cases
    if (n <= 0) return 0;
    if (n == 1) return 1;

    // Check if already calculated
    if (memo.find(n) != memo.end()) {
        return memo[n];
    }

    // Calculate and store in memo
    memo[n] = fibonacci(n - 1) + fibonacci(n - 2);
    return memo[n];
}

