def find_median_two_sorted_arrays(arr1, arr2):
    n1, n2 = len(arr1), len(arr2)

    # If the first array is empty, return the median of the second array.
    if n1 == 0:
        return median(arr2)
    elif n2 == 0:
        return median(arr1)

    # Initialize pointers to the beginning of each array.
    i = 0
    j = 0

    # Find the median by partitioning the arrays and comparing elements.
    if n1 < n2:
        return find_median_helper(arr1, arr2, i, j, n1, n2)
    else:
        return find_median_helper(arr2, arr1, j, i, n2, n1)

def find_median_helper(arr1, arr2, i, j, n1, n2):
    if i == n1:
        return median(arr2[j:])  # Return the median of the remaining elements in arr2.

    if j == n2:
        return median(arr1[i:])  # Return the median of the remaining elements in arr1.

    if arr1[i] < arr2[j]:
        i += 1
        return find_median_helper(arr1, arr2, i, j, n1, n2)

    # Swap and continue partitioning.
    j += 1
    return find_median_helper(arr1, arr2, i, j, n1, n2)

def median(arr):
    length = len(arr)
    middle_idx = length // 2
    if length % 2 == 0:
        return (arr[middle_idx - 1] + arr[middle_idx]) / 2
    return arr[middle_idx]
