def count_full_binary_trees(n):
    def recur(n):
        if n == 0:
            return 1
        if n == 1:
            return 0  # There's no tree with one vertex (root without any children)
        result = 0
        for i in range(n):
            left_subtrees = recur(i)  # Number of full trees with i left nodes
            right_subtrees = recur(n - i - 1)  # Number of full trees with n-i-1 right nodes
            # Each node can have 2 children, so multiply the counts of left and right subtrees
            result += left_subtrees * right_subtrees
        return result

    return recur(n)

