#include <iostream>

using namespace std;

// Recursive function to calculate the nth Fibonacci number
int fibonacci(int n) {
  // Base cases
  if (n == 0) {
    return 0;
  } else if (n == 1) {
    return 1;
  } else {
    // Recursive step: nth Fibonacci number is the sum of the (n-1)th and (n-2)th Fibonacci numbers
    return fibonacci(n - 1) + fibonacci(n - 2);
  }
}

int main() {
  int n;

  cout << "Enter the value of n: ";
  cin >> n;

  cout << "The " << n << "th Fibonacci number is: " << fibonacci(n) << endl;

  return 0;
}
```

