def full_binary_trees(n):
    if n == 0:
        return 1
    elif n == 1:
        return 0
    else:
        B = [0] * (n + 1)
        B[0] = 1
        for i in range(1, n + 1):
            for j in range(i):
                B[i] += B[j] * B[i - j - 1]
        return B[n]
