#include <iostream>

using namespace std;

int fibonacci(int n) {
  if (n == 0 || n == 1)
    return n;
  return fibonacci(n-1) + fibonacci(n-2);
}

int main() {
  int n, i;
  cout << "Enter the nth Fibonacci number: ";
  cin >> n;
  i = fibonacci(n);
  cout << "The nth Fibonacci number is: " << i << endl;
