        return arr1[start1 + k]

    mid1 = (start1 + end1) // 2
    mid2 = (start2 + end2) // 2

    if arr1[mid1] < arr2[mid2]:
        return find_kth_element(arr1, mid1 + 1, end1, arr2, start2, end2, k)
    elif arr1[mid1] > arr2[mid2]:
        return find_kth_element(arr1, start1, mid1 - 1, arr2, mid2 + 1, end2, k)
