#include <iostream>

int fibonacci(int n) {
    if (n <= 1) {
        return n;
    }
    return fibonacci(n - 1) + fibonacci(n - 2);
}

int main() {
    int n = 0;
    std::cout << "Enter the index of the Fibonacci number you want to find: ";
    std::cin >> n;

    int fib_num = fibonacci(n);

    std::cout << "The " << n << "th Fibonacci number is: " << fib_num << std::endl;

    return 0;
}
```
