def find_kth_smallest_in_union(lists, k):
    if len(lists) == 0:
        return -1

    m, n = len(lists[0]), len(lists[1])
    if m < n:
        i, j = 0, 0
    else:
        i, j = 0, m

    while i < m and j < n:
        if lists[0][i] <= lists[1][j]:
            i += 1
        else:
            j += 1

    if i >= m:
        return lists[1][j]
    elif j >= n:
        return lists[0][i]
    else:
        k_remaining = k - (j + 1)
        return find_kth_smallest_in_list(lists[1], k_remaining)

def find_kth_smallest_in_list(list_, k):
    if len(list_) == 0:
        return -1

    low, high = 0, len(list_) - 1
    if k < (high + low) // 2:
        return binary_search(list_, k, low, high)
    else:
        return find_kth_smallest_in_list(partition(list_), k)

def partition(lst):
    pivot = lst[-1]
    i = 0
    for num in lst[:-1]:
        if num < pivot:
            i += 1
        else:
            break
    return lst[:i+1], lst[i+1:]

def binary_search(lst, target, low, high):
    while low <= high:
        mid = (low + high) // 2
        if lst[mid] == target:
            return lst[mid]
        elif lst[mid] < target:
            low = mid + 1
        else:
            high = mid - 1
    return -1
```
