    if not list1:
        return list2[k]
    if not list2:
        return list1[k]

    len1, len2 = len(list1), len(list2)

    # Base cases
    if k < 0 or k > len1 + len2:
        raise ValueError("k is out of bounds")
    if k == 0:
        return min(list1[0], list2[0])
    if k == len1 + len2 - 1:
        return max(list1[-1], list2[-1])

    # Binary search
    low, high = 0, len1 + len2 - 1
    while low <= high:
        mid = (low + high) // 2
        left = min(mid, len1 - 1)
        right = mid - left - 1

        left_val = list1[left] if left >= 0 else float('inf')
        right_val = list2[right] if right >= 0 else float('inf')

        if left_val <= right_val:
            list1, list2 = list2, list1
            len1, len2 = len2, len1
            k = k - left - 1
            low, high = 0, len2 - 1
        else:
            high = mid - 1

    return list2[k]
